import React, { Component } from "react";
import { Fragment } from "react";
import axios from 'axios';

export default class Category extends Component {
  state = {
    category: []
  }

  componentDidMount() {
    axios.get('https://dummyjson.com/products/categories').then((result) => {
      this.setState({
        category: result.data
      })
    });
  }

  render() {
    return (
      <Fragment>
        <section className="section-featured--default ps-home--block categories">
          <div className="container">
            <div className="ps-block__header">
              <h3 className="ps-block__title">Kategori Terbaik</h3><a className="ps-block__view" href="/category">View all<i className="icon-chevron-right"></i></a>
            </div>
            <div className="featured--content">
              <div className="featured__group" style={{ width: '100%' }}>
                <div className="row m-0">
                  {
                    this.state.category.map(category => {
                      return <Category categoryUrl={category} categoryTitle={category} categoryThumbnail={"https://medicaldialogues.in/h-upload/2022/01/21/168768-category.webp"} categorySumProduct={15} productUrl={category}></Category>
                    })
                  }
                </div>
              </div>
            </div>
          </div>
        </section>
      </Fragment >
    )
  }
}
