import React, { Component } from "react";
import { Fragment } from "react";
import axios from 'axios';

import Product from "../../component/Product/Product";

import { BrowserRouter as Link, Routes, Route, redirect, useNavigate, useHistory } from "react-router-dom";
import Category from "../../component/Category/Category";

export default class Home extends Component {
  state = {
    product: [],
    category: [],
  }

  componentDidMount() {
    axios.get('https://dummyjson.com/products').then((result) => {
      this.setState({
        product: result.data.products.splice(0, 28)
      })
    });

    axios.get('https://dummyjson.com/products/categories').then((result) => {
      this.setState({
        category: result.data.splice(0, 9)
      })
    });
  }

  handleDetail = (id, slug) => {
    // console.log("handleDetail", id + " clicked " + slug);
    // redirect(`/detail/${id}/${slug}`);
    // console.log(redirect(`/detail/${id}/${slug}`));
    console.log(navigate(`/detail/${id}/${slug}`));
  }

  render() {
    return (
      <Fragment>
        <section className="section-featured--default ps-home--block categories">
          <div className="container">
            <div className="ps-block__header">
              <h3 className="ps-block__title">Kategori Terbaik</h3>
            </div>
            <div className="featured--content">
              <div className="featured__first">
                {
                  <div className="ps-product--vertical">
                    <a onClick={() => this.handleDetailCategory(this.state.category[0])}>
                      <img className="ps-product__thumbnail" src="https://medicaldialogues.in/h-upload/2022/01/21/168768-category.webp" alt={this.state.category[0]} title={this.state.category[0]} />
                    </a>
                    <div className="ps-product__content"><a className="ps-product__name" onClick={() => this.handleDetailCategory(this.state.category[0])}>{this.state.category[0]}</a>
                      <p className="ps-product__quantity">11 produk</p>
                    </div>
                  </div>
                }
              </div>
              <div className="featured__group">
                <div className="row m-0">
                  {
                    this.state.category.slice(1).map(category => {
                      return <Category goDetailCategory={this.handleDetailCategory} categoryUrl={category} categoryTitle={category} categoryThumbnail={"https://medicaldialogues.in/h-upload/2022/01/21/168768-category.webp"} categorySumProduct={15} productUrl={category} />
                    })
                  }
                </div>
              </div>
            </div>
          </div>
        </section>
        <section className="section-shop shop-categories--default">
          <div className="container">
            <div className="ps-block__header">
              <h3 className="ps-block__title">Produk Terbaru</h3>
            </div>
            <div className="result__content">
              <div className="section-shop--grid">
                <div className="row m-0">
                  {
                    this.state.product.map(product => {
                      return <Product goDetail={this.handleDetail} storeName={product.brand} productId={product.id} productTaxonomy={product.title} productPrice={product.price} productImg={product.thumbnail} productUrl={product.title.toLowerCase().replace(/ /g, '-').replace(/[^\w-]+/g, '')} ></Product>
                    })
                  }
                </div>
              </div>
            </div>
          </div>
        </section>
      </Fragment>
    )
  }
}
